import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Button, Alert, Spinner, Badge } from 'react-bootstrap';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const BookingDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [booking, setBooking] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    // If not logged in, redirect to login
    if (!user) {
      navigate('/login');
      return;
    }

    const fetchBookingDetails = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL || 'http://localhost:8000'}/api/bookings/${id}`
        );
        setBooking(response.data.data.booking);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching booking details:', err);
        setError('Failed to load booking details');
        setLoading(false);
      }
    };

    fetchBookingDetails();
  }, [id, user, navigate]);

  // Format date
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  // Format time
  const formatTime = (dateString) => {
    const options = { hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleTimeString(undefined, options);
  };

  if (loading) {
    return (
      <Container className="my-5 text-center">
        <Spinner animation="border" variant="primary" />
        <p className="mt-3">Loading booking details...</p>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="my-5">
        <Alert variant="danger">{error}</Alert>
        <Button variant="secondary" onClick={() => navigate('/profile')}>
          Back to Profile
        </Button>
      </Container>
    );
  }

  if (!booking) {
    return (
      <Container className="my-5">
        <Alert variant="warning">Booking not found</Alert>
        <Button variant="secondary" onClick={() => navigate('/profile')}>
          Back to Profile
        </Button>
      </Container>
    );
  }

  return (
    <Container className="my-5">
      <Row>
        <Col>
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h2>Booking Details</h2>
            <Button variant="outline-secondary" onClick={() => navigate('/profile')}>
              Back to Profile
            </Button>
          </div>

          <Card className="mb-4">
            <Card.Header className="bg-primary text-white">
              <h4 className="mb-0">Booking #{booking._id}</h4>
            </Card.Header>
            <Card.Body>
              <Row>
                <Col md={8}>
                  <h3>{booking.tour?.title || 'Tour'}</h3>
                  <p className="text-muted">{booking.tour?.location || ''}</p>

                  <hr />

                  <Row className="mb-3">
                    <Col sm={4}><strong>Booking Date:</strong></Col>
                    <Col sm={8}>{formatDate(booking.date)}</Col>
                  </Row>

                  <Row className="mb-3">
                    <Col sm={4}><strong>Participants:</strong></Col>
                    <Col sm={8}>{booking.participants}</Col>
                  </Row>

                  <Row className="mb-3">
                    <Col sm={4}><strong>Status:</strong></Col>
                    <Col sm={8}>
                      <Badge bg={booking.status === 'confirmed' ? 'success' : 'secondary'}>
                        {booking.status.toUpperCase()}
                      </Badge>
                    </Col>
                  </Row>

                  {booking.tour && (
                    <>
                      <Row className="mb-3">
                        <Col sm={4}><strong>Tour Duration:</strong></Col>
                        <Col sm={8}>{booking.tour.duration} days</Col>
                      </Row>

                      <Row className="mb-3">
                        <Col sm={4}><strong>Tour Price:</strong></Col>
                        <Col sm={8}>${booking.tour.price} per person</Col>
                      </Row>

                      <Row className="mb-3">
                        <Col sm={4}><strong>Total Amount:</strong></Col>
                        <Col sm={8}>${booking.tour.price * booking.participants}</Col>
                      </Row>
                    </>
                  )}

                  {booking.specialRequests && (
                    <Row className="mb-3">
                      <Col sm={4}><strong>Special Requests:</strong></Col>
                      <Col sm={8}>{booking.specialRequests}</Col>
                    </Row>
                  )}

                  <Row className="mb-3">
                    <Col sm={4}><strong>Booked On:</strong></Col>
                    <Col sm={8}>{formatDate(booking.createdAt)} at {formatTime(booking.createdAt)}</Col>
                  </Row>
                </Col>

                <Col md={4}>
                  <Card className="border-primary">
                    <Card.Header className="bg-primary text-white">
                      Contact Information
                    </Card.Header>
                    <Card.Body>
                      <p>
                        <strong>Name:</strong><br />
                        {booking.fullName}
                      </p>
                      <p>
                        <strong>Email:</strong><br />
                        {booking.email}
                      </p>
                      {booking.phone && (
                        <p>
                          <strong>Phone:</strong><br />
                          {booking.phone}
                        </p>
                      )}
                    </Card.Body>
                  </Card>

                  <div className="mt-4">
                    {booking.status === 'confirmed' && new Date(booking.date) > new Date() && (
                      <Button variant="danger" className="w-100 mb-2">
                        Cancel Booking
                      </Button>
                    )}
                    <Button variant="outline-primary" className="w-100">
                      Download Booking Details
                    </Button>
                  </div>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default BookingDetails;
