import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

const TourDetails = () => {
  const { id } = useParams();
  const [tour, setTour] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTourDetails = async () => {
      try {
        const response = await axios.get(`/api/tours/${id}`);
        setTour(response.data.data.tour);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch tour details. Please try again later.');
        setLoading(false);
        console.error('Error fetching tour details:', err);
      }
    };

    fetchTourDetails();
  }, [id]);

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      </div>
    );
  }

  if (!tour) {
    return (
      <div className="container mt-5">
        <div className="alert alert-info" role="alert">
          Tour not found.
        </div>
        <Link to="/tours" className="btn btn-primary">
          Back to Tours
        </Link>
      </div>
    );
  }

  return (
    <div className="container my-5">
      <div className="row">
        <div className="col-lg-8">
          <h1>{tour.title}</h1>
          <p className="lead">{tour.location}</p>

          <div className="my-4">
            <div className="bg-light p-4 rounded">
              <div className="row">
                <div className="col-md-6">
                  <h5>Tour Details</h5>
                  <ul className="list-unstyled">
                    <li><strong>Duration:</strong> {tour.duration} days</li>
                    <li><strong>Price:</strong> ${tour.price}</li>
                    <li><strong>Location:</strong> {tour.location}</li>
                  </ul>
                </div>
                <div className="col-md-6">
                  <h5>Highlights</h5>
                  <ul>
                    <li>All-inclusive package</li>
                    <li>Professional tour guide</li>
                    <li>Comfortable accommodations</li>
                    <li>Daily breakfast and dinner</li>
                  </ul>
                </div>
              </div>
            </div>
          </div>

          <h4>Tour Description</h4>
          <p>
            Experience the beauty and culture of {tour.location} on this {tour.duration}-day adventure. 
            This tour offers a perfect blend of sightseeing, relaxation, and cultural immersion.
            From historical landmarks to natural wonders, this tour covers all the must-see attractions
            in {tour.location}.
          </p>

          <h4>Itinerary</h4>
          <div className="mb-4">
            <div className="card mb-3">
              <div className="card-body">
                <h5 className="card-title">Day 1: Arrival and Welcome</h5>
                <p className="card-text">
                  Arrive at your destination and check into your hotel. Meet your tour guide and fellow travelers
                  during a welcome dinner at a local restaurant.
                </p>
              </div>
            </div>
            <div className="card mb-3">
              <div className="card-body">
                <h5 className="card-title">Day 2: City Exploration</h5>
                <p className="card-text">
                  Visit the main attractions of the city with a knowledgeable local guide. Enjoy lunch at a famous
                  local establishment and continue exploring in the afternoon.
                </p>
              </div>
            </div>
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">Day 3 and Beyond</h5>
                <p className="card-text">
                  Explore famous landmarks, natural wonders, and immerse yourself in the local culture.
                  Detailed itinerary will be provided upon booking.
                </p>
              </div>
            </div>
          </div>
        </div>

        <div className="col-lg-4">
          <div className="card sticky-top" style={{ top: '20px' }}>
            <div className="card-body">
              <h3 className="card-title">${tour.price}</h3>
              <p className="card-text text-muted">per person</p>
              <hr />
              <div className="d-grid">
                <Link to={`/booking/${tour.id}`} className="btn btn-primary btn-lg">
                  Book Now
                </Link>
              </div>
              <hr />
              <div className="small text-muted">
                <p><strong>Not sure yet?</strong> You can reserve a spot now and pay later.</p>
                <p>Free cancellation up to 24 hours before the tour starts.</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TourDetails;
