import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Button, Alert, Spinner, Badge, Modal } from 'react-bootstrap';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useReactToPrint } from 'react-to-print';

const BookingDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [booking, setBooking] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [cancelLoading, setCancelLoading] = useState(false);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [cancelSuccess, setCancelSuccess] = useState(false);
  const [cancelError, setCancelError] = useState('');

  // Ref for printing
  const printRef = useRef();

  // Handle print functionality
  const handlePrint = useReactToPrint({
    content: () => printRef.current,
    documentTitle: `Booking_${id}`,
    onBeforeGetContent: () => {
      return new Promise((resolve) => {
        resolve();
      });
    },
    onAfterPrint: () => {
      console.log('Print completed');
    }
  });

  useEffect(() => {
    // If not logged in, redirect to login
    if (!user) {
      navigate('/login');
      return;
    }

    const fetchBookingDetails = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL || 'http://localhost:8000'}/api/bookings/${id}`
        );
        setBooking(response.data.data.booking);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching booking details:', err);
        setError('Failed to load booking details');
        setLoading(false);
      }
    };

    fetchBookingDetails();
  }, [id, user, navigate]);

  // Format date
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  // Format time
  const formatTime = (dateString) => {
    const options = { hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleTimeString(undefined, options);
  };

  // Handle cancel booking
  const handleCancelBooking = async () => {
    setCancelLoading(true);
    setCancelError('');

    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL || 'http://localhost:8000'}/api/bookings/${id}/cancel`
      );

      setCancelSuccess(true);

      // Update the booking status in the state
      setBooking(prev => ({
        ...prev,
        status: 'cancelled'
      }));

    } catch (err) {
      console.error('Error cancelling booking:', err);
      setCancelError(err.response?.data?.message || 'Failed to cancel booking');
    } finally {
      setCancelLoading(false);
    }
  };

  if (loading) {
    return (
      <Container className="my-5 text-center">
        <Spinner animation="border" variant="primary" />
        <p className="mt-3">Loading booking details...</p>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="my-5">
        <Alert variant="danger">{error}</Alert>
        <Button variant="secondary" onClick={() => navigate('/profile')}>
          Back to Profile
        </Button>
      </Container>
    );
  }

  if (!booking) {
    return (
      <Container className="my-5">
        <Alert variant="warning">Booking not found</Alert>
        <Button variant="secondary" onClick={() => navigate('/profile')}>
          Back to Profile
        </Button>
      </Container>
    );
  }

  return (
    <>
      <Container className="my-5">
        <Row>
          <Col>
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h2>Booking Details</h2>
              <Button variant="outline-secondary" onClick={() => navigate('/profile')}>
                Back to Profile
              </Button>
            </div>

            <div ref={printRef}>
              <Card className="mb-4">
                <Card.Header className="bg-primary text-white">
                  <h4 className="mb-0">Booking #{booking._id}</h4>
                </Card.Header>
                <Card.Body>
                  <Row>
                    <Col md={8}>
                      <h3>{booking.tour?.title || 'Tour'}</h3>
                      <p className="text-muted">{booking.tour?.location || ''}</p>

                      <hr />

                      <Row className="mb-3">
                        <Col sm={4}><strong>Booking Date:</strong></Col>
                        <Col sm={8}>{formatDate(booking.date)}</Col>
                      </Row>

                      <Row className="mb-3">
                        <Col sm={4}><strong>Participants:</strong></Col>
                        <Col sm={8}>{booking.participants}</Col>
                      </Row>

                      <Row className="mb-3">
                        <Col sm={4}><strong>Status:</strong></Col>
                        <Col sm={8}>
                          <Badge bg={
                            booking.status === 'confirmed' ? 'success' : 
                            booking.status === 'cancelled' ? 'danger' : 'secondary'
                          }>
                            {booking.status.toUpperCase()}
                          </Badge>
                        </Col>
                      </Row>

                      {booking.tour && (
                        <>
                          <Row className="mb-3">
                            <Col sm={4}><strong>Tour Duration:</strong></Col>
                            <Col sm={8}>{booking.tour.duration} days</Col>
                          </Row>

                          <Row className="mb-3">
                            <Col sm={4}><strong>Tour Price:</strong></Col>
                            <Col sm={8}>${booking.tour.price} per person</Col>
                          </Row>

                          <Row className="mb-3">
                            <Col sm={4}><strong>Total Amount:</strong></Col>
                            <Col sm={8}>${booking.tour.price * booking.participants}</Col>
                          </Row>
                        </>
                      )}

                      {booking.specialRequests && (
                        <Row className="mb-3">
                          <Col sm={4}><strong>Special Requests:</strong></Col>
                          <Col sm={8}>{booking.specialRequests}</Col>
                        </Row>
                      )}

                      <Row className="mb-3">
                        <Col sm={4}><strong>Booked On:</strong></Col>
                        <Col sm={8}>{formatDate(booking.createdAt)} at {formatTime(booking.createdAt)}</Col>
                      </Row>
                    </Col>

                    <Col md={4}>
                      <Card className="border-primary">
                        <Card.Header className="bg-primary text-white">
                          Contact Information
                        </Card.Header>
                        <Card.Body>
                          <p>
                            <strong>Name:</strong><br />
                            {booking.fullName}
                          </p>
                          <p>
                            <strong>Email:</strong><br />
                            {booking.email}
                          </p>
                          {booking.phone && (
                            <p>
                              <strong>Phone:</strong><br />
                              {booking.phone}
                            </p>
                          )}
                        </Card.Body>
                      </Card>

                      <div className="mt-4 d-print-none">
                        {booking.status === 'confirmed' && new Date(booking.date) > new Date() && (
                          <Button 
                            variant="danger" 
                            className="w-100 mb-2"
                            onClick={() => setShowCancelModal(true)}
                          >
                            Cancel Booking
                          </Button>
                        )}
                        <Button 
                          variant="outline-primary" 
                          className="w-100"
                          onClick={handlePrint}
                        >
                          <i className="bi bi-printer me-2"></i>
                          Print Booking Details
                        </Button>
                      </div>
                    </Col>
                  </Row>
                </Card.Body>
              </Card>
            </div>
          </Col>
        </Row>
      </Container>

      {/* Cancel Booking Modal */}
      <Modal show={showCancelModal} onHide={() => !cancelLoading && setShowCancelModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Cancel Booking</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {cancelSuccess ? (
            <Alert variant="success">
              <h5>Booking Cancelled Successfully</h5>
              <p>Your booking has been cancelled. A confirmation email will be sent to your registered email address.</p>
            </Alert>
          ) : (
            <>
              {cancelError && <Alert variant="danger">{cancelError}</Alert>}
              <p>Are you sure you want to cancel this booking?</p>
              <p><strong>Tour:</strong> {booking.tour?.title}</p>
              <p><strong>Date:</strong> {formatDate(booking.date)}</p>
              <p><strong>Participants:</strong> {booking.participants}</p>
              <Alert variant="warning">
                <i className="bi bi-exclamation-triangle me-2"></i>
                This action cannot be undone. Please contact customer support if you cancel by mistake.
              </Alert>
            </>
          )}
        </Modal.Body>
        <Modal.Footer>
          {cancelSuccess ? (
            <Button variant="primary" onClick={() => navigate('/profile')}>
              Back to Profile
            </Button>
          ) : (
            <>
              <Button 
                variant="secondary" 
                onClick={() => setShowCancelModal(false)}
                disabled={cancelLoading}
              >
                No, Keep Booking
              </Button>
              <Button 
                variant="danger" 
                onClick={handleCancelBooking}
                disabled={cancelLoading}
              >
                {cancelLoading ? (
                  <>
                    <Spinner
                      as="span"
                      animation="border"
                      size="sm"
                      role="status"
                      aria-hidden="true"
                      className="me-2"
                    />
                    Cancelling...
                  </>
                ) : (
                  'Yes, Cancel Booking'
                )}
              </Button>
            </>
          )}
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default BookingDetails;
