import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, Typography, Card, CardContent, Badge } from '@mui/material';
import StarIcon from '@mui/icons-material/Star';

const UserPoints = ({ userId }) => {
  const [points, setPoints] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserPoints = async () => {
      if (!userId) {
        setLoading(false);
        return;
      }

      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL || 'http://localhost:8000'}/api/users/${userId}/points`);
        setPoints(response.data.data.points);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching user points:', err);
        setError('Failed to load your loyalty points');
        setLoading(false);
      }
    };

    fetchUserPoints();
  }, [userId]);

  if (loading) {
    return <Box>Loading points...</Box>;
  }

  if (error) {
    return <Box color="error.main">{error}</Box>;
  }

  if (!userId) {
    return null;
  }

  return (
    <Card variant="outlined" sx={{ mb: 2, bgcolor: '#f5f5f5' }}>
      <CardContent>
        <Box display="flex" alignItems="center">
          <Badge
            badgeContent={points}
            color="primary"
            showZero
            sx={{ 
              '& .MuiBadge-badge': { 
                fontSize: '1rem', 
                height: 'auto', 
                padding: '5px 10px'
              }
            }}
          >
            <StarIcon color="warning" fontSize="large" sx={{ mr: 1 }} />
          </Badge>
          <Box ml={2}>
            <Typography variant="h6">Loyalty Points</Typography>
            <Typography variant="body2" color="text.secondary">
              {points === 0 ? 
                'Book a tour to earn points!' : 
                `You have ${points} points to use for future discounts!`}
            </Typography>
          </Box>
        </Box>
      </CardContent>
    </Card>
  );
};

export default UserPoints;
